import React, {useEffect} from 'react'
import Dice from "../Components/Dice";
import Button from 'react-bootstrap/Button'

import "./Home.scss"

const Home: React.FC<{}> = () => {
    let diceOneChoice: number = getRandomInt(5);
    let diceTwoChoice: number = getRandomInt(5);

    let diceChoice = active;

    useEffect(() => {
        diceChoice = active;
    }, [active])

    const onClickDiceRoll = () => {
        diceOneChoice = getRandomInt(5);
        diceTwoChoice = getRandomInt(5);
        console.log(diceOneChoice)
        console.log(diceTwoChoice)
    }

    return (
        <div className="Home">
            <h1>Disc Golf Dice</h1>
            <br />
            <Dice header={"Disc choice"} active={diceOneChoice} options={["Putter", "Mid", "Driver", "Understable", "Overstable", "Wild"]} />
            <Dice header={"Shot choice"} active={diceTwoChoice} options={["Backhand", "Forehand", "Roller", "Hyzer", "Anhyzer", "Wild"]} />
            <Button variant="dark" size="lg" block onClick={onClickDiceRoll}>
                Roll dices
            </Button>
        </div>
    )
};

function getRandomInt(max: number) {
    return Math.floor(Math.random() * Math.floor(max));
}

export default Home
