import React, {useState} from 'react'
import Dice from "../Components/Dice";
import Button from 'react-bootstrap/Button'

import "./Home.scss"

const Home: React.FC<{}> = () => {
    const [diceOneChoice, setDiceOneChoice] = useState(0);
    const [diceTwoChoice, setDiceTwoChoice] = useState(0);

    const onClickDiceRoll = () => {
        setDiceOneChoice(7);
        setDiceTwoChoice(7);

        setTimeout(function(){
            setDiceOneChoice(getRandomInt(5)+1);
            setDiceTwoChoice(getRandomInt(5)+1);
            }, 1000)
    }

    return (
        <div className="Home">
            <h1>Disc Golf Dice</h1>
            <br />
            <Dice header={"Disc choice"} active={diceOneChoice} options={["Putter", "Mid", "Driver", "Understable", "Overstable", "Wild"]} />
            <Dice header={"Shot choice"} active={diceTwoChoice} options={["Backhand", "Forehand", "Roller", "Hyzer", "Anhyzer", "Wild"]} />
            <Button variant="dark" size="lg" block onClick={onClickDiceRoll}>
                Roll dices
            </Button>
        </div>
    )
};

function getRandomInt(max: number) {
    return Math.floor(Math.random() * Math.floor(max));
}

export default Home
