import React, {useEffect} from "react";
import "./Dice.scss"

type DiceProps = {
    header: string,
    options: string[],
    active: number
}

const Dice: React.FC<DiceProps> = ({header, options, active}) => {

    const diceChoice = active;

    useEffect(() => {
        diceChoice = active;
    }, [active])

    return (
        <div className="Dice">
            <h4>{header}</h4>
            <div className="Square">
                <h5>{options[diceChoice]}</h5>
            </div>
        </div>
    )
};

export default Dice
